// testimonial js start

let testimonialIndex = 0;
let testimonialTimer;
const testimonialContainer = document.querySelector(".testimonial-container");
const testimonialDots = document.querySelectorAll(
  ".testimonial-indicators [data-bs-slide-to]"
);

function showTestimonials(n) {
  testimonialIndex = n;
  if (testimonialIndex >= 3) testimonialIndex = 0;
  if (testimonialIndex < 0) testimonialIndex = 2;

  // Update slide position
  testimonialContainer.style.transform = `translateX(-${
    testimonialIndex * 33.333
  }%)`;

  // Update indicators
  testimonialDots.forEach((dot, index) => {
    dot.classList.toggle("active", index === testimonialIndex);
  });
}

function autoSlideTestimonials() {
  testimonialIndex++;
  showTestimonials(testimonialIndex);
}

function resetTestimonialTimer() {
  clearInterval(testimonialTimer);
  testimonialTimer = setInterval(autoSlideTestimonials, 3000);
}

// Add click events to indicator dots
testimonialDots.forEach((dot, index) => {
  dot.addEventListener("click", () => {
    showTestimonials(index);
    resetTestimonialTimer();
  });
});

// Initialize slider
showTestimonials(testimonialIndex);
resetTestimonialTimer();
// testimonial js end

// Initialize Swiper
let swiper = new Swiper(".productsSwiper", {
  slidesPerView: 1,
  spaceBetween: 30,
  autoplay: {
    delay: 3000,
    disableOnInteraction: false,
  },
  pagination: {
    margin: 50,
    el: ".swiper-pagination",

    clickable: true,
    dots : true,
  },
  breakpoints: {
    640: {
      slidesPerView: 1,
    },
    768: {
      slidesPerView: 2,
    },
    1024: {
      slidesPerView: 3,
    },
  },
});

// Category filtering
document
  .querySelectorAll(".product-filter-buttons-2 .btn ")
  .forEach((button) => {
    button.addEventListener("click", () => {
      const filter = button.getAttribute("data-filter");

      // Update active button
      document
        .querySelectorAll(".product-filter-buttons-2 .btn")
        .forEach((btn) => {
          btn.classList.remove("active");
        });
      button.classList.add("active");

      // Filter slides
      const slides = document.querySelectorAll(".swiper-slide");
      slides.forEach((slide) => {
        if (
          filter === "all" ||
          slide.getAttribute("data-category").includes(filter)
        ) {
          slide.style.display = "";
        } else {
          slide.style.display = "none";
        }
      });

      // Update swiper
      swiper.update();
      swiper.slideTo(0);
    });
  });
